module Main where

import Data.List

moveFloor :: Int -> Char -> Int
moveFloor current char
    | char == '(' = current + 1
    | char == ')' = current - 1
    | otherwise = error "Invalid char"

positions :: String -> [Int]
positions str = 0 : zipWith moveFloor (positions str) str

